import React, { Component } from 'react';

class DropDown extends Component {
	constructor(props) {
		super(props);
		if (localStorage.getItem('listArray') == null) {
			this.state = {
				nameTask: '',
				list: [],
				done: false
			}
		} else {
			this.state = {
				nameTask: '',
				list: JSON.parse(localStorage.getItem('listArray')),
				done: false
			}
		}
	}
	handleSubmit(event) {
		event.preventDefault();

			let listArray = this.state.list;
			listArray.push(this.state.nameTask);

			localStorage.setItem('listArray', JSON.stringify(listArray));

		this.setState({
			nameTask: '',
			list: JSON.parse(localStorage.getItem('listArray'))
			});
	}

	handleNameChange(event) {
		this.setState({nameTask: event.target.value});
	}

	markDone() {
		this.setState({
			done: !this.state.done
		});
	}

	deleteTask() {
		console.log('del');

		this.setState({
			list: this.state.list.splice(index, 1)
		})
	}

	render() {
		console.log(this.state.done);

		let done;
		if (this.state.done) {
			done = {textDecoration: 'line-through'}
		} 
		return (
			<div>
				<form onSubmit={this.handleSubmit.bind(this)}>
					<label>
						<p>Введите имя задачи</p>
						<input 
						type="text"
						placeholder="Имя задачи"
						value={this.state.nameTask}
						onChange={this.handleNameChange.bind(this)} 
						 />
					</label>
					<button>Создать задачу</button>
				</form>
				<div>
					<ul>
					{this.state.list.map((task, index) => 
						<li key={index} 
						style={done}
						>
						{task}
						<button onClick={this.markDone.bind(this)}>Done</button>
						<button onClick={this.deleteTask.bind(this)}>Delete</button>
						</li>
						)}
					</ul>
				</div>
			</div>
			);
	}
}

export default DropDown;
