import React, { Component } from 'react';

class DropDown extends Component {
	constructor(props) {
		super(props);
		this.state = {
			isValue: false
		};
	}
	toogleClick(){
		this.setState({isValue: !this.state.isValue});
	}

	render() {
		console.log('isValue: ', this.state.isValue);
		let block;
		if (this.state.isValue) {
			block = <p>буэ...</p>
		} else 
			block = <p>Введите имя задачи</p>
		return (
			<div className='name'>
				<label>
					<p>Введите имя задачи:</p>
					<input type="text" onClick={this.toogleClick.bind(this)} />
				</label>
			</div>
			);
	}
}

export default DropDown;
