import React, { Component } from 'react';

class DropDown extends Component {
	constructor(props) {
		super(props);
		this.state = {
			nameTask: '',
			list: []
		};
	}
	handleSubmit(event) {
		event.preventDefault();
		this.setState({nameTask: ''});
		if (localStorage.getItem('listArray') == null) {
			let listArray = this.state.list;
			listArray.push(this.state.nameTask);

			this.setState({
			list: listArray
			});
			localStorage.setItem('listArray', JSON.stringify(listArray));
		} else {

		}
		
		console.log('submit', this.state.nameTask);
	}

	handleNameChange(event) {
		
		this.setState({nameTask: event.target.value});
		console.log(event.target.value);
	}

	render() {
		return (
			<div>
				<form onSubmit={this.handleSubmit.bind(this)}>
					<label>
						<p>Введите имя задачи</p>
						<input 
						type="text"
						placeholder="Имя задачи"
						value={this.state.nameTask}
						onChange={this.handleNameChange.bind(this)} 
						 />
					</label>
					<button>Submit</button>
				</form>
				<div>
					{this.state.list.map((task, index) => 
							<ul>
								<li key={index}>{task}</li>
							</ul>
						)}
				</div>
			</div>
			);
	}
}

export default DropDown;
