import React, { Component } from 'react';

class DropDown extends Component {
	constructor(props) {
		super(props);
		this.state = {
			isValue: false
		};
	}
	toogleClick(){
		this.setState({isValue: !this.state.isValue});
	}

	render() {
		return (
			<div className='name'>
				<label>
					<p>Введите имя задачи:</p>
					<input type="text" onChange={this.toogleClick()} />
				</label>
			</div>
			);
	}
}

export default DropDown;
